import React, { useState } from "react";
import Conditions from "./Conditions";
import classes from "./Forecast.module.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { CardGroup, Card } from "react-bootstrap";

const Forecast = () => {
  let [city, setCity] = useState("");
  let [unit, setUnit] = useState("imperial");
  let [responseObj, setResponseObj] = useState({});
  let [error, setError] = useState(false);
  let [loading, setLoading] = useState(false);

  const uriEncodedCity = encodeURIComponent(city);

  function getForecast(e) {
    e.preventDefault();

    if (city.length === 0) {
      return setError(true);
    }

    // Clear state in preparation for new data
    setError(false);
    setResponseObj({});
    setLoading(true);

    fetch(
      // Current Forecast
      `https://community-open-weather-map.p.rapidapi.com/weather?units=${unit}&q=${uriEncodedCity}`,
      // `https://community-open-weather-map.p.rapidapi.com/forecast?q=${uriEncodedCity}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
          //   "x-rapidapi-key": process.env.REACT_APP_API_KEY,
          "x-rapidapi-key":
            "b54663a5e6msh64cb43d3524bb21p186d22jsnb6eb1989079a",
        },
      }
    )
      .then((response) => response.json())
      .then((response) => {
        console.log(response);
        if (response.cod !== 200) {
          throw new Error();
        }
        setResponseObj(response);
        setLoading(false);
      })
      .catch((err) => {
        setError(true);
        setLoading(false);
        console.log(err.message);
      });
  }

  return (
    <div>
      <Card>
        <Card.Body>
          <Card.Title>Find Current Weather</Card.Title>

          <form onSubmit={getForecast}>
            <input
              type="text"
              placeholder="Enter City"
              maxLength="50"
              className={classes.textInput}
              value={city}
              onChange={(e) => setCity(e.target.value)}
            />
            <label className={classes.Radio}>
              <input
                type="radio"
                name="units"
                checked={unit === "imperial"}
                value="imperial"
                onChange={(e) => setUnit(e.target.value)}
              />
              Fahrenheit
            </label>
            <label className={classes.Radio}>
              <input
                type="radio"
                name="units"
                checked={unit === "metric"}
                value="metric"
                onChange={(e) => setUnit(e.target.value)}
              />
              Celcius
            </label>

            <button className={classes.Button} type="submit">
              Get Forecast
            </button>
          </form>

          <Conditions
            responseObj={responseObj}
            error={error}
            loading={loading}
          />
        </Card.Body>
      </Card>
    </div>
  );
};

export default Forecast;
