import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { CardGroup, Card } from "react-bootstrap";
// import TextEditor from "../src/TextEditor/TextEditor.js";

// import "./textEditor.css";

import "devextreme/dist/css/dx.common.css";
import "./themes/generated/theme.base.css";
import "./themes/generated/theme.additional.css";
import "./dx-styles.scss";

import HtmlEditor, {
  Toolbar,
  MediaResizing,
  Item,
} from "devextreme-react/html-editor";

class SecondGroup extends React.Component {
  constructor() {
    super();

    this.sizeValues = ["8pt", "10pt", "12pt", "14pt"];
    this.fontValues = [
      "Arial",
      "Courier New",
      "Lucida Console",
      "Tahoma",
      "Times New Roman",
      "Verdana",
    ];
    // this.headerValues = [false, 1, 2, 3, 4, 5];
    this.enabled = {
      enabled: true,
    };
  }

  render() {
    return (
      <div>
        <CardGroup>
          <CardGroup>
            <Card>
              <Card.Body>
                <Card.Title>5-day Weather Forecast</Card.Title>

                <Card>
                  <Card.Body>
                    <Card.Title>Day 1</Card.Title>
                    <Card.Text>Monday</Card.Text>
                  </Card.Body>
                </Card>
              </Card.Body>
            </Card>
          </CardGroup>
          <Card>
            <Card.Body>
              <Card.Title>Post-It</Card.Title>
              <React.Fragment>
                <div className="widget-container">
                  <HtmlEditor height="380px">
                    <MediaResizing enabled={true} />
                    <Toolbar>
                      <Item formatName="separator" />
                      <Item formatName="size" formatValues={this.sizeValues} />
                      <Item formatName="font" formatValues={this.fontValues} />
                      <Item formatName="separator" />
                      <Item formatName="bold" />
                      <Item formatName="italic" />
                      <Item formatName="strike" />
                      <Item formatName="underline" />
                      <Item formatName="separator" />
                      <Item formatName="alignLeft" />
                      <Item formatName="alignCenter" />
                      <Item formatName="alignRight" />
                      <Item formatName="alignJustify" />
                      <Item formatName="separator" />
                      <Item formatName="orderedList" />
                      <Item formatName="bulletList" />
                      <Item formatName="separator" />
                      <Item formatName="color" />
                      <Item formatName="background" />
                      <Item formatName="separator" />
                      <Item formatName="link" />
                      <Item formatName="image" />
                      <Item formatName="separator" />
                    </Toolbar>
                  </HtmlEditor>
                </div>
              </React.Fragment>
            </Card.Body>
          </Card>
        </CardGroup>
      </div>
    );
  }
}

export default SecondGroup;
