import React from "react";
import classes from "./textEditor.module.css";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import Button from "react-bootstrap/Button";

import "devextreme/dist/css/dx.common.css";
import "../themes/generated/theme.base.css";
import "../themes/generated/theme.additional.css";
import "../dx-styles.scss";

import HtmlEditor, {
  Toolbar,
  MediaResizing,
  Item,
} from "devextreme-react/html-editor";

const TextEditor = () => {
  const [text, setText] = React.useState();
  const handleSave = async () => {
    const response = await axios.post("http://localhost:5000/postit", {
      text: "I'm from the client.",
    });
    console.log(response);
  };

  const sizeValues = ["8pt", "10pt", "12pt", "14pt"];
  const fontValues = [
    "Arial",
    "Courier New",
    "Lucida Console",
    "Tahoma",
    "Times New Roman",
    "Verdana",
  ];
  // this.headerValues = [false, 1, 2, 3, 4, 5];
  const enabled = {
    enabled: true,
  };

  return (
    <div className={classes.postitApp}>
      <React.Fragment>
        <div className="widget-container">
          <h5>Post-It</h5>
          <HtmlEditor height="380px" value={text} onValueChanged={() => {}}>
            <MediaResizing enabled={true} />
            <Toolbar>
              <Item formatName="separator" />
              <Item formatName="size" formatValues={sizeValues} />
              <Item formatName="font" formatValues={fontValues} />
              <Item formatName="separator" />
              <Item formatName="bold" />
              <Item formatName="italic" />
              <Item formatName="strike" />
              <Item formatName="underline" />
              <Item formatName="separator" />
              <Item formatName="alignLeft" />
              <Item formatName="alignCenter" />
              <Item formatName="alignRight" />
              <Item formatName="alignJustify" />
              <Item formatName="separator" />
              <Item formatName="orderedList" />
              <Item formatName="bulletList" />
              <Item formatName="separator" />
              <Item formatName="color" />
              <Item formatName="background" />
              <Item formatName="separator" />
              <Item formatName="link" />
              <Item formatName="image" />
              <Item formatName="separator" />
            </Toolbar>
          </HtmlEditor>
          <Button variant="secondary" onClick={handleSave}>
            SAVE
          </Button>{" "}
          <Button variant="danger">DELETE</Button>
        </div>
      </React.Fragment>
    </div>
  );
};

export default TextEditor;
