import React, { Component } from "react";
import GoogleLogin from "react-google-login";

const CLIENT_ID =
  "522668337222-7ec4busvjmku0dbnpe3s6l951b0mtdd2.apps.googleusercontent.com";

// Array of API discovery doc URLs for APIs used by the quickstart
var DISCOVERY_DOCS = [
  "https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest",
];

// Authorization scopes required by the API; multiple scopes can be
// included, separated by spaces.
var SCOPES = "https://www.googleapis.com/auth/calendar.readonly";

var authorizeButton = document.getElementById("authorize_button");
var signoutButton = document.getElementById("signout_button");

/**
 *  Called when the signed in status changes, to update the UI
 *  appropriately. After a sign-in, the API is called.
 */
function updateSigninStatus(isSignedIn) {
  if (isSignedIn) {
    authorizeButton.style.display = "none";
    signoutButton.style.display = "block";
    listUpcomingEvents();
  } else {
    authorizeButton.style.display = "block";
    signoutButton.style.display = "none";
  }
}

/**
 *  Sign in the user upon button click.
 */
function handleAuthClick(event) {
  window.gapi.auth2.getAuthInstance().signIn();
}

/**
 *  Sign out the user upon button click.
 */
function handleSignoutClick(event) {
  window.gapi.auth2.getAuthInstance().signOut();
}

/**
 * Append a pre element to the body containing the given message
 * as its text node. Used to display the results of the API call.
 *
 * @param {string} message Text to be placed in pre element.
 */
function appendPre(message) {
  var pre = document.getElementById("content");
  var textContent = document.createTextNode(message + "\n");
  pre.appendChild(textContent);
}

/**
 * Print the summary and start datetime/date of the next ten events in
 * the authorized user's calendar. If no events are found an
 * appropriate message is printed.
 */
function listUpcomingEvents() {
  window.gapi.client.calendar.events
    .list({
      calendarId: "primary",
      timeMin: new Date().toISOString(),
      showDeleted: false,
      singleEvents: true,
      maxResults: 10,
      orderBy: "startTime",
    })
    .then(function (response) {
      var events = response.result.items;
      appendPre("Upcoming events:");

      if (events.length > 0) {
        for (let i = 0; i < events.length; i++) {
          var event = events[i];
          var when = event.start.dateTime;
          if (!when) {
            when = event.start.date;
          }
          appendPre(event.summary + " (" + when + ")");
        }
      } else {
        appendPre("No upcoming events found.");
      }
    });
}

function initClient() {
  console.log(window.gapi);
  window.gapi.client
    .init({
      apiKey: "AIzaSyBIa7trpR0-6xMIs44G5PkTqGtE71O1jLU",
      clientId: CLIENT_ID,
      discoveryDocs: DISCOVERY_DOCS,
      scope: SCOPES,
    })
    .then(
      function () {
        console.log("Client is connected");
        // Listen for sign-in state changes.
        window.gapi.auth2
          .getAuthInstance()
          .isSignedIn.listen(updateSigninStatus);

        // Handle the initial sign-in state.
        updateSigninStatus(
          window.gapi.auth2.getAuthInstance().isSignedIn.get()
        );
      },
      function (error) {
        appendPre(JSON.stringify(error, null, 2));
      }
    );
}

class GoogleOAuth extends Component {
  componentDidMount() {
    console.log("XXXX - mounting");
    window.gapi.load("client:auth2", initClient);
  }

  responseGoogle = (response) => {
    // const imageURL = response.profileObj.imageUrl;
    const email = response.profileObj.email;
    console.log(email);
    // const name = response.profileObj.givenName;
    const accessToken = response.accessToken;
    console.log(accessToken);
    const tokenObj = response.tokenObj;
    console.log(tokenObj);
  };

  render() {
    return (
      <div className="GoogleOAuth">
        <button onClick={handleAuthClick} id="authorize_button">
          Authorize
        </button>
        <button onClick={handleSignoutClick} id="signout_button">
          Sign Out
        </button>
        <GoogleLogin
          clientId={CLIENT_ID}
          buttonText="Login"
          onSuccess={this.responseGoogle}
          onFailure={this.responseGoogle}
          // cookiePolicy={"single_host_origin"}
        />
      </div>
    );
  }
}

export default GoogleOAuth;
